void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy, p = (u - .5 * r) / r.y * 8.;
    o -= o;

    vec2 c = floor(p);
    p.x += .5 * mod(c.y, 2.);

    o += mod(floor(p.x), 2.);
    p -= c;

    o = mix(o, vec2(1, 0).rggg, smoothstep(16. / r.y, 0., abs(max(abs(fract(p.x) - .5), abs(p.y - .5)) - .5)));
}
