void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float d1=clamp(2.0*sin(distance(fragCoord.xy,iMouse.xy)/10.),-1.0,1.0);
    float d2=clamp(2.0*sin(distance(fragCoord.xy,abs(iMouse.zw))/10.),-1.0,1.0);
    float f=d1*d2*.5;    
    fragColor = vec4(.5-f,.5+f,.5-f,1.0);
}
