vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord*iResolution.xy)/iResolution.y;

    float zoom = 1.0 / 30.0;
    float largeZoom = 10.0;

    float timeSize = 20.0;
    float windPower = 1.0 / 50.0;

    float wind = (noise(vec2(iTime * timeSize / 100.0, 0)) - 0.5) * windPower;
    vec2 windDirection = vec2(1, 0);

    vec2 offset = normalize(windDirection) * vec2(iTime, iTime) * vec2(wind, wind);

    vec2 channelRes = iChannelResolution[0].xy;
    float textureThing = noise(uv * vec2(zoom / largeZoom, zoom / largeZoom));
    float extraTexture = noise(uv * vec2(zoom, zoom) + offset);

    float outCol = 0.0;

    outCol += textureThing * 0.75;
    outCol += extraTexture * 0.25;

    vec4 col = vec4(0.0, 0.0, 0.0, 1.0);

    if (outCol <= 0.15 && outCol >= 0.05) {
        col.r = 1.0;
    }

    if (outCol <= 0.25 && outCol >= 0.15) {
        col.g = 1.0;
    }

    if (outCol >= 0.25) {
        col.b = 1.0;
    }

    fragColor = col;
}
