#define NUM 100.
#define EXPLOSIONS 3.
#define CLOCK_SPEED .2

vec2 Hash12(float t){

float x=fract(sin(t*345.34)*356.3);
float y=fract(sin((t+x)*567.34)*789.23);

return vec2(x,y);

}

vec2 Hash12polar(float t){

float a=fract(sin(t*345.34)*356.3)*564.7;
float r=fract(sin((t+a)*567.34)*789.23);

return vec2(sin(a),cos(a))*r;

}

float Explosion(vec2 uv, float t){
float sparks=0.;

for(float i=0.; i < NUM; i++){

    vec2 dir =Hash12polar(i+1.);

    float d= length(uv-dir*t+vec2(0,t*t));

    float brightness= mix(.01,.001,smoothstep(.0,.1,t));

    brightness*=sin(t*27.7+i)*.25+.75;
    brightness*=smoothstep(1.,.5,t);

    sparks+=brightness/d;

}

return sparks;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col =vec3(0);


    float t=iTime*CLOCK_SPEED;
    float ft=floor(t);


    for(float i=0.; i < EXPLOSIONS; i++){
            vec3 color= sin(vec3(.34,.67,.19)*3.*ft*i)*.5+.5;

            vec2 offs=Hash12(i+1.+ft)-.5;
            offs*=vec2(1.77,1.);
            col+=Explosion(uv-offs, fract(t+i*.23))*color;

    }

    col*=2.;

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;



vec4 old= texture(iChannel1, uv)*.95; //fade
vec4 new= texture(iChannel0, uv);



    fragColor = max(old, new);}
