float drawQuad(vec2 uv, vec2 pos, float a, float b, float blur){
    vec2 border = vec2((1.-a)/2., (1.-b)/2.);
    vec2 toOrigin = vec2(border.x + a/2., border.y + b/2.);
    vec2 offset = toOrigin - pos;
    vec2 bl = smoothstep(border-offset-blur, border-offset, uv);
    vec2 tr = smoothstep(border+offset-blur, border+offset, 1.-uv);
    return bl.x * bl.y * tr.x * tr.y;
}

vec3 smoothEdge(vec3 shape, vec3 bgcolor, vec3 color){
    vec3 tintedShape = shape * color;
    vec3 edgeColor = mix(bgcolor + tintedShape, tintedShape, shape.x);
    return mix(bgcolor, edgeColor, shape.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2. * (fragCoord.xy - 0.5 * iResolution.xy)/ min(iResolution.x, iResolution.y);

    vec3 pink = vec3(1.,0.,0.5);

    vec3 green = vec3(0.,1.,0.5);

    vec3 col = pink;

    vec3 quad = vec3(drawQuad(uv, vec2(0.,0.), 1., 0.6, 0.01));

    col = smoothEdge(quad, col, green);

    fragColor = vec4(col,1.0);
}
