#define TAU (6.283185307)
#define STRIPECOUNT 8.

#define HEX(x) vec3((ivec3(x) >> ivec3(16, 8, 0)) & 255) / 255.

vec3 getColor(float r, float aa) {
    //r = fract(r);
    float mix0 = smoothstep(0., aa, r);
    float mix1 = smoothstep(0., aa, r - 0.25);
    float mix2 = smoothstep(0., aa, r - 0.5);
    float mix3 = smoothstep(0., aa, r - 0.75);

    vec3 color0 = HEX(0x009BE8);
    vec3 color1 = HEX(0xEB0072);
    vec3 color2 = HEX(0xfff100);
    vec3 color3 = HEX(0x000000);

    return (
        color0 * (mix0 - mix1) +
        color1 * (mix1 - mix2) +
        color2 * (mix2 - mix3) +
        color3 * (mix3 - mix0 + 1.)
    );
}


float zigzag(float x) {
    return abs(1. - 2. * fract(x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float time = fract(0.25 + iTime / 4.);
  // Normalized pixel coordinates (from 0 to 1)
  float scale = min(iResolution.x, iResolution.y);
  vec2 uv = (fragCoord.xy - 0.50 * iResolution.xy) / scale;

  float dist = length(uv);
  float angle = atan(uv.y, uv.x);

  float aaWidth = 3. * min(
    fwidth(angle), fwidth(atan(uv.x, uv.y))
  );

  float angleSway = 0.8 * sin(time * TAU);
  float angleSpin = 0.5 * cos(time * TAU);

  // for calculating the stripesâ€™ angle
  float angleL = angleSway + (
      floor(0.5 +
          angle * STRIPECOUNT / TAU - angleSpin
      ) + angleSpin
  ) * TAU / STRIPECOUNT;
  float angleD = angleSway + (
      0.5 + floor(
          angle * STRIPECOUNT / TAU - angleSpin
      ) + angleSpin
  ) * TAU / STRIPECOUNT;

  vec2 angleVecL = vec2(cos(angleL), sin(angleL));
  vec2 angleVecD = vec2(cos(angleD), sin(angleD));

// for calculating which color palette to use
  float dark = smoothstep(
      -aaWidth, aaWidth,
      24. * ( -0.5 +
          zigzag( 0.5 +
              angle * STRIPECOUNT / TAU
              - angleSpin
          )
      ) / STRIPECOUNT
  );

  float distScale = 1.6 + 0.4 * cos(2. * time * TAU);

  float spiralL = fract(
      distScale * (-1. + log(dot(uv, angleVecL) * 2. + 0.005)) + 0. * time
  );
  float spiralD = fract(
      -0.25 * (1. - cos(2. * time * TAU)) +
      distScale * (-1. + log(dot(uv, angleVecD) * 2. + 0.005)) + 0. * time
  );

  vec3 colL = getColor(spiralL, aaWidth);
  vec3 colD = getColor(spiralD, aaWidth);

  vec3 col = mix(colL, colD, dark);
  // Output to screen
  fragColor = vec4(
      col, 1.0
  );
}
