/**
Simple Spanish flag, without coat of arms.

Based on
https://www.shadertoy.com/view/3lyGRd
*/

#define PI 3.1415926535

#define RED  vec3(250., 21., 27.) / 255.
#define YELOW  vec3(241., 171, 0) / 255.



// Hash by Dave_Hoskins
float hash(vec2 p)
{
	uvec2 q = uvec2(ivec2(p)) * uvec2(1597334673U, 3812015801U);
	uint n = (q.x ^ q.y) * 1597334673U;
	return float(n) / float(0xffffffffU);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 st = fragCoord / iResolution.xy;
	float w = sin((uv.x + uv.y - iTime * 1.0 + sin(2.5 * uv.x + 4.5 * uv.y) * PI * .3)
                  * PI * .6); // fake waviness factor

    uv *= 1. + (.036 - .036 * w);
    vec3 col = YELOW;

    // flag colors
    col += 1.;
    col = mix(col, RED, smoothstep(.45, .46, uv.y));
    col = mix(col, RED, smoothstep(-.45, -.46, uv.y));
    col += w * .225;

    float v = 10. * st.x * (1. - st.x) * st.y * (1. - st.y); // vignette
	col *= 0.7 - .5 * exp2(-2.75 * v);
    col = clamp(col - hash(fragCoord) * .05, 0., 1.);
    fragColor = vec4(col, 1.);
}
