//fast vec2 -> vec4 hash function for webgl
//github.com/victor-shepardson

//hash based on Blum, Blum & Shub 1986
//and Sharpe http://briansharpe.wordpress.com/2011/10/01/gpu-texture-free-noise/
float pi = 3.14159265359;
float bbsm = 1739.;
vec2 bbsopt(in vec2 a){
	return fract(a*a*(1./bbsm))*bbsm;
}
vec2 mod1024(in vec2 a){
	return fract(a*(1./1024.))*1024.;	
}
vec4 hash(in vec2 pos){
	vec2 a0 = mod1024(pos*pi);
	vec2 a1 = bbsopt(a0);
	vec2 a2 = a1.yx + bbsopt(a1);
	vec2 a3 = a2.yx + bbsopt(a2);
	return fract((a2.xyxy + a3.xxyy + a1.xyyx)*(1./bbsm));
}

vec4 fbm(in vec2 pos){
    float m = 1.;
    float n = 0.125;
    vec4 r = vec4(0.);
    for (int i=0; i<32; i++){
        r += hash(pos) * n;
        m *= r.x;
        n *= 0.9;
        pos *= 1.5;
    }
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const vec2 vel = vec2(60., 60.); //animation speed
    const vec2 scale = vec2(1., 1.);
	const int num = 1; //average multiple instances

	vec2 p = scale*floor(fragCoord.xy-vel*iTime);

	vec4 c = vec4(0.);
	for(int i=0; i<num; i++)
		c+= fbm(p*pow(sin(iTime)*.5+2.,5.)*0.0000001+float(i*1024));
	c*=1./float(num);

	fragColor = vec4(c.x,c.y,c.z,c.w);

}
