vec2 rotatev2(vec2 p, float ang)
{
    float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}


float Hash21(vec2 p)
{
    p=fract(p*vec2(234.34,435.345));
    p+=dot(p,p+34.26);
    return fract(p.x*p.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv*= 7.0;
    //uv+=iTime;
    uv+=vec2(0.0, iTime*1.5);

    uv= rotatev2(uv, radians(45.0));

    vec3 col=vec3(0.0);
    vec2 gv=fract(uv)-0.5;
    vec2 id=floor(uv);

    float n=Hash21(id); //rango 0.0 a 1.0

    float width=0.1;

    if(n<0.5) gv.x*=-1.0;

    //float d=abs(gv.x+gv.y); //diagonal...linea

//----------
/// Tip Shane....9/jun/2021
float d=abs(gv.x + gv.y)*.7071; // Diagonal line.
gv = abs(gv) - .5; // Corners.
d = min(d, abs(gv.x + gv.y)*.7071); 
//----------


    float  mask=smoothstep(0.01,-0.01, d-width);


    float r = pow(1.0-sqrt( d),1.5 );
    float g = pow(1.0-sqrt( d),1.5 );
    float b = 1.0*(r+g);



    col+=vec3(r,g-0.8,b-0.8)*mask;


    //if(gv.x>0.48 || gv.y>0.48) col=vec3(1.0,0.0,.0);
    fragColor=vec4(col,1.0);
 }
