float rect(vec2 uv, float l, float r, float b, float t, float blur){
    float c = clamp(-(uv.x-r)/(.05*blur),0.,1.);
    c -= clamp(-(uv.x-l)/(1.*blur),0.,1.);
    c -= clamp(-(uv.y-b)/(.5*blur),0.,1.);
    c -= clamp((uv.y-t)/(.5*blur),0.,1.);
    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    //uv.x += uv.y*0.2;
    float fx = uv.x;
    float fy = uv.y + sin(8.*(fx+iTime))*.1;
    //x += y*0.2-0.1;
    //y += (x-0.5)*(x+0.5); 
    float blur = clamp(fx,0.05,0.3);
    float c = rect(vec2(fx,fy),-0.5,0.5,-0.1,0.1,blur);
    // Output to screen
    fragColor = vec4(vec3(c),1.0);
}
