// Fork of "Refraction 1" by tekt. https://shadertoy.com/view/ftfGWX
// 2021-06-07 01:49:09

// Based on https://www.youtube.com/watch?v=NCpaaLkmXI8



// Forked from "RayMarching starting point" by BigWIngs. https://shadertoy.com/view/WtGXDD

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}


// Repeat the domain only in positive direction. Everything in the negative half-space is unchanged.
float pMod1(inout float p, float size) {
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize, size) - halfsize;
	return c;
}

float smoothBlendRatio(float a, float b, float k) {
	return clamp(0.5 + 0.5*(b-a)/k, 0.0, 1.0);
}

float opSmoothUnionM(float d1, float d2, float k) {
	float h = smoothBlendRatio(d1, d2, k);
	return mix(d2, d1, h) - k*h*(1.0-h);
}
float fTorus(vec3 p, float smallRadius, float largeRadius) {
	return length(vec2(length(p.xz) - largeRadius, p.y)) - smallRadius;
}


float shape(vec3 p) {
pR(p.xy, 1.123);

    for (int i = 0; i <3; i++) {
        p.xz = abs(p.xz);

        pR(p.xy, 0.8*sin(iTime * 0.1));
//        p.x += 0.1;

        pR(p.xz, 0.6*sin(0.2 + iTime * 0.5));
        p = abs(p);
    }

    float d = sdBox(p, vec3(1.1));

    return d;
}

float GetDist(vec3 p) {
    float d = shape(p);

//    float d1 = -sdBox(p, vec3(100., 3., 100.));
    float d1 = -fTorus(p, 6.8, 7.0);
    d = opSmoothUnionM(d, d1, .5);

    return d;
}

float RayMarch(vec3 ro, vec3 rd, float side) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p) * side;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
//	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 0.5, -6);
//    ro.yz *= Rot(-m.y*3.14+1.);
//    ro.xz *= Rot(-m.x*6.2831);

    pR(ro.xz, iTime*0.1);
    pR(ro.xy, -0.2);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = texture(iChannel0, rd).rgb;

    float d = RayMarch(ro, rd, 1.0); // outside of object

float IOR = 1.45; // higher= denser
    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 reflDir = reflect(rd, n); // reflected dir

        // when going from less dense to more dense, need to use inverse of IOR
        vec3 rayDirIn = refract(rd, n, 1.0/IOR); // refracted dir

        vec3 pEnter = p - n*SURF_DIST*3.;// arbitrary multiplier to make it a little bigger
        float dInside = RayMarch(pEnter, rayDirIn, -1.); // inside the object

        vec3 pExit = pEnter + rayDirIn * dInside; // 3d position of exit
        vec3 nExit = -GetNormal(pExit);

        vec3 rayDirOut = refract(rayDirIn, nExit, IOR);
        // dot product with self is like length() but without sqrt(), which is good enough to check for 0
        if (dot(rayDirOut, rayDirOut) == 0.) {
            rayDirOut = reflect(rayDirIn, nExit);
        }

//        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
//        col = vec3(dif);
        vec3 reflTex = texture(iChannel0, rayDirOut).rgb;
        col = reflTex;

        col *= 6.-dInside;
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
