    float C(float x, float y, float r) { // circle's function
            return x*x + y*y - r;
    }
void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = 1.0 - 2.0 * fragCoord/iResolution.xy;
     float x = p.x * iResolution.x/iResolution.y;
     float y = p.y;
      float f = 10.0*C(3.0*x, 3.0*y, 1.5);              //body
      f = min (f, 20.0*C(3.0*x-0.5, 4.0*y-1.75, 0.15)); //lef leg
      f = min (f, 20.0*C(3.0*x+0.5, 4.0*y-1.75, 0.15)); //right leg 
      f = min (f, 50.0*C(3.0*x-0.8, 4.0*y+1.35, 0.04)); //left ear
      f = min (f, 50.0*C(3.0*x+0.8, 4.0*y+1.35, 0.04)); // right ear
      f = min (f, 20.0*C(5.0*x-2.1, 4.0*y-0.2, 0.24));  //left hand
      f = min (f, 20.0*C(5.0*x+2.1, 4.0*y-0.2, 0.24));  //right hand
      f /= 0.05/C(6.0*x+0.5, 6.0*y+0.8, 0.24); //right eye
      f /= 0.05/C(7.0*x+0.5, 7.0*y+0.8, 0.09); 
      f /= 0.05/C(6.0*x-0.5, 6.0*y+0.8, 0.24); //left eye
      f /= 0.05/C(7.0*x-0.5, 7.0*y+0.8, 0.09); 
      f /= 0.05/C(6.0*x+0.0, 10.0*y+0.4, 0.26); //nose
      f /= 0.05/C(6.0*x+0.0, 20.0*y-1.3, 0.26); //mouse
              vec3 col;
              if (f <= 1.0)col.r = -f;
              fragColor = vec4(col, 1.0);
}
