void mainImage( out vec4 O, vec2 U )
{
    float l = floor(2.*log2(R.y)),          // for jigsaw between min-max LOD
          t = floor(l*fract(iTime/8.-.05));
          t = min(t,l-t);

    O = T(U, t );
    O = sin( O/fwidth(O) );                 // normalized isolines
 // O = sin( 10.* O );                      // without normalization

}

void mainImage( out vec4 O, vec2 U )
{
    O = step( T(U,0.), vec4(.3) );            // mask 
}
