float ring(vec2 p, vec2 point, float r, float blur) {
        float b=length(p-point);
         float c=smoothstep(r,r-blur,b);
          return c;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = 1.0 * fragCoord.xy/iResolution.xy;
     p-=.5;
     p.x*=iResolution.x/iResolution.y;
      float c=ring(p, vec2(.0, .0), .2, .01);//цв
       c+=ring(p, vec2(-.3, .1), .2, .01);//лв
       c+=ring(p, vec2(.3, .1), .2, .01);//пв
       c+=ring(p, vec2(.15, -.39), .1, .01);//пн
       c+=ring(p, vec2(-.15, -.39), .1, .01);//лн
       c+=ring(p, vec2(.0, -.30), .15, .01);//цн
       c-=ring(p, vec2(-.3, .1), .1, .01);//лвц
       c-=ring(p, vec2(.3, .1), .1, .01);//пвц
       c-=ring(p, vec2(-.06, .05), .05, .01);//лвцц
       c-=ring(p, vec2(.06, .05), .05, .01);//пвцц

        vec3 col=vec3(1., 1., 0.)*c;
         fragColor = vec4(col, 1.0);
}
