void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv.x = sin(uv.x*2.0) * 0.5 + 0.5;
    //uv.y = sin(uv.y*4.0) * 0.5 + 0.5;
    //uv = 0.4 * uv + vec2(0.3, 0.3);
    uv.x = uv.x + 0.3 * sin(uv.y * 4.0 * sin(iTime * 1.2) + uv.x * 4.0 * cos(iTime * 1.4));
    uv.y = uv.y + 0.3 * sin(uv.y * 4.0 * cos(iTime * 1.3) + uv.x * 4.0 * sin(iTime * 1.5));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    col = texture(iChannel0, uv).xyz;
    fragColor = vec4(col,1.0);
}
