float ring(vec2 p, vec2 point, float r, float blur) {
        float b=length(p-point);
         float c=smoothstep(r,r-blur,b);
          return c;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = 1.0 * fragCoord.xy/iResolution.xy;
     p-=.5;
     p.x*=iResolution.x/iResolution.y;
      float c=ring(p, vec2(.0,- .1), .2, .01);//ц
       c+=ring(p, vec2(.10, -.20), .1, .01);//пн
       c+=ring(p, vec2(.13, -.07), .1, .01);//пв
       c+=ring(p, vec2(-.10, -.20), .1, .01);//лн
       c+=ring(p, vec2(-.13, -.07), .1, .01);//лв
       c+=ring(p, vec2(-.07, -.32), .05, .01);//нога л
       c+=ring(p, vec2(.07, -.32), .05, .01);//нога п
       c+=ring(p, vec2(.07, .05), .1, .01);//пвв
       c+=ring(p, vec2(-.07, .05), .1, .01);//лвв
       c-=ring(p, vec2(-.03, -.05), .02, .01);//лвцц
       c-=ring(p, vec2(.03, -.05), .02, .01);//пвцц

        vec3 col=vec3(1., 0., 1.)*c;
         fragColor = vec4(col, 1.0);
}
