vec4 bg = vec4(0.5,0.7,1,1.0);
vec4 fg = vec4(0.5,0.8,1,1.0);

float circle_radius = 0.3;
float pattern_radius = 0.5;

float circle(vec2 p, float r)
{
	return length(p) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 p = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

	float t = iTime;
	float dist = 1.0471975;
	float sdf = 0.0;

	for (int i = 0; i < 6; i++) {
		float x = sin(t) * pattern_radius * cos(dist * float(i)) + p.x;
		float y = sin(t) * pattern_radius * sin(dist * float(i)) + p.y;
		float circ = circle(vec2(x, y), circle_radius * sin(0.75 * (t + 0.4 * float(i))));
		sdf = min(sdf, circ);
	}

    bg.r = sin(t * 0.1) * 0.5;
    fg.r = cos(t * 1.1) * 0.5;
    fragColor = mix(bg.rgba, fg.rgba, fg.a * (1.0 - step(0.0, sdf)));
}
