#define LIMIT (5000.0)
#define CENTER (vec2(-1.7499924942284, -0.0093919724293))
#define CS (0.244)
#define TS (-0.17)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = exp(TS * iTime + 1.0);
    vec2 c = fragCoord;
    c.x -= (iResolution.x - iResolution.y);
    c /= iResolution.y;
    c = CENTER + (c * 2.0 - 1.0) * zoom;

    float i;
    vec2 z = vec2(0.0);
    for (i = 0.0; i < LIMIT; ++i) {
        z = c + vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y);
        if (dot(z, z) > 4.0) break;
    }
    vec3 col = vec3(0.5);
    col += cos(CS * i) * vec3(0.0, 0.5, -0.5);
    col += sin(CS * i) * vec3(-1.0, 0.5, 0.5) * sqrt(3.0);
    fragColor = vec4(i == LIMIT ? vec3(0.0) : col, 1.0);
}
