// Codegolfed a bit for fun
void mainImage(out vec4 c, in vec2 p) {
    vec2 r = iResolution.xy;
    p = round((p - .5 * r) / r.y / .01) * .01;

    c = vec4(1);
    float d = dot(p, p) - .125;
    c.b = step(0., d);
    c -= step(0., .0075 - abs(d));

    vec2 u = vec2(abs(p.x) - .125, p.y - .1);
    c -= step(0., 1e-5 - dot(u * u * u, u));

    u = vec2(abs(p.x + .02) - .125, u.y - .02);
    c = max(c, 0.) + step(0., 5e-4 - dot(u, u));

    u = vec2(p.x, p.y + .2 * cos(5. * p.x) - .01);
    u.x -= clamp(u.x, -.2, .2);
    c -= step(0., 2e-4 - dot(u, u));

    u = vec2(abs(p.x) - .2, p.y + .01);
    c = mix(c, vec4(1, 0, 0, 0), .24 - 12. * dot(u, u));
}
