float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv += 0.5;
    vec2 guv = uv;




    float leng = 2.0;
    vec2 xy = vec2(0,0);
    xy.x = uv.x * pow(uv.y + .5, 1.0);
    xy.x *= leng;
    float rollSpeed = iTime * 3.0;


    float dist = 1.5;
    float t = 0.5;
    xy.x = sin(xy.x * dist - cos(xy.x * dist + rollSpeed) + rollSpeed) - t;
    xy.x = map(xy.x,-t, t,0.0, 1.0);
    xy.x = pow(xy.x, 1.0);
    fragColor.r = xy.x;
    fragColor.g = .0;
    return;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+guv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
