// Use the mouse to move around the XOR Towers.
// Use the 'w' & 's' keys to move in/out.
// A big thanks to Fabrice Neyret for helping me figure out the Z motion algorithm.
void mainImage(out vec4 o, vec2 u)
{ 
    ivec4 b = ivec4(o -= o);
    u = 2.*(u+u-R)/R.y;

    for (; (b.x^b.y^ b.z+int(T.z) ) % 99-int(T.z) > b.z-8 ; )
        b = ivec4(u * o.a + T.xy, o+=.1 ); 

    o = o/70. - .02*vec4(b%3);
}

#define k(c) texelFetch(iChannel1,ivec2(c,0),0).x > 0.
void mainImage( out vec4 o,vec2 u )
{
    o = T;
    k(87) ? o++ : k(83) ? o-- : o;

    o.xy =  abs(T.xy + iMouse.xy / R - .5);   
}
