// Fork of "Visual Enhancer" by scry. https://shadertoy.com/view/tlfyDf
// 2022-04-03 06:54:20

float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 R = iResolution.xy;
    float ar = R.x/R.y;
    uv -= 0.5;
    uv.x *= ar;
    uv += 0.5;
    float c = length(uv-0.5);
    float tt = iTime;
    float n1 = mod(noise(uv*950.)*100.,1.)*100.;
    float n2 = noise(uv+n1+tt*0.1);
    vec3 col = vec3(1.-(n2*1.));
    col *= 0.99;
    col += 0.0;
    vec3 c2 = 1.-col;
    float fn = noise(uv);
    float f =(sin((tt*3.14)*9.+sin(tt*.4)*44.)+1.)*0.1;
    float s =(sin(tt*0.04)+1.);
    f = f+0.95;
    s += 1.;
    col = fract(col*1.02);
    col *= 0.35;
    col += f*0.04; //fast flicker for dreamachine effect
    //https://en.wikipedia.org/wiki/Dreamachine
    col *= s*0.5;
    col *= 0.95 +sin(c*10.+tt)*0.02;
    col += (sin(uv.y*6.+tt*0.033)*0.5+0.5)*.04;
    float ds = 100.;
    vec2 ov = uv;
    //uv.x += uv.y*2.;
    uv.x += uv.y*0.5;
    uv = (fract(uv*ds*R.x)-0.5)/ds;
    uv.x -= uv.y*0.5;
    col += step(length(uv),0.003)*0.1;
    uv = ov;
    ds = 10.;
    uv.x += uv.y*0.5;
    uv = (fract(uv*R.x/30.)-0.5)/ds;
    uv.x -= uv.y*0.5;
    col += step(length(uv),0.006)*0.12;
    uv = ov;
    ds = 14.;
    //uv.x += uv.y*0.5;
    uv = (fract(uv*ds*R.x/200.)-0.5)/ds;
    //uv.x -= uv.y*0.5;
    col += step(length(uv),0.02)*0.02;
    //col = mix(col,c2,f);
    //col = mod(col,1.);
    //gl_FragColor = vec4(col,1.);
    // Normalized pixel coordinates (from 0 to 1)


    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
