/*
Nothign fancy, just like the effect :) 

*/float box(vec3 p,vec3 b){
    vec3 q = abs(p)-b;
    return length(max(vec3(0.),q))+min(0.,max(q.x,max(q.y,q.z)));
  }
  vec3 erot(vec3 p,vec3 ax,float a){
     return mix(dot(ax,p)*ax,p,cos(a))+cross(ax,p)*sin(a);
    }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
 vec3 col = vec3(.0);
  vec3 p,d=normalize(vec3(uv,1.));
	float i,e,g;
  for(i=g=e=0.;i++<128.;){
     p =d*g;
     p.z -=15.;
      p = erot(p,vec3(0.,0.,1.),p.z*.1+iTime*.25);
    float dd,c=16./3.1415;
    p.xz = vec2(log(dd=length(p.xz)),atan(p.z,p.x))*c;
    p.y /=dd/=c;
    p.xz = asin(sin(p.xz-iTime));
     p = erot(p,vec3(0.,1.,0.),.1);
     float sc = 1.,scp=1.01;
      float h= 1000.; 
    for(float i=0.;i<4.;i++){
       p*=scp;
       sc*=scp;
       p.xz = abs(p.xz)-.25;
       p.xy = p.x > p.y ? p.xy:p.yx;
       p = erot(p,normalize(vec3(.5,1.,1.)),.785);
        h= min(box(p-.5,vec3(2.,.1,2.1))/sc,h);
       h = min(h, (length(p.xz)-.51)/sc);
     }
     h = dd*.9*min(h,box(p,vec3(1.))/sc);
     g+=e=max(.01,abs(h));
     col +=vec3(.0,.12-abs(dot(asin(sin(p*5.)),asin(cos(p.zxy*7.))))*.03,.0)*(sin(iTime*2.+uv.y*2.*iResolution.y))/exp(.125*i*i*e);

    }
    fragColor = vec4(col,1.0);
}
