vec4 mixLod(sampler2D tex, vec2 uv, float lod, float lod_step){
    float counter = 0.0;
    float times = 1.0;
    vec3 col = vec3(1.0, 1.0, 1.0);
    while(counter < lod){
        col = mix(col, textureLod(tex, uv, counter).rgb, 1.0 / times);
        counter += lod_step;
        times += 1.0;
    }
    return vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord / iResolution.xy;
    float control = ((1.0 - (iMouse.x / iResolution.x))*5.0) + 0.1;

    fragColor = mixLod(iChannel0, uv, control, .5);
}
