float stars(vec2 uv,float scale)
{
	uv*=scale;
    vec2 s=floor(uv);
    vec2 f=fract(uv);
    float k=1.;
	vec2 p =.4+.35*sin(35.*fract(sin((s+25. *iTime*0.00003)*mat2(2.0,1.0,3.0,4.0))))-f;
    float d=length(p);
    k=min(d,k);
	k=smoothstep(0.,k,sin(f.x+f.y)*0.005);
   	return k;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float li = (texture(iChannel0, uv * 0.08 +iTime*0.008).r * 10. -2.);

    float v = uv.y + li * 0.1;
    v =1. - abs(v * 2.0 - 1.);
    v = pow(abs(v), 2.0 + sin((iTime * 0.25 + li * 0.5)));

    vec3 startColor = vec3(0.05, 0.1, 0.1);
    vec3 endColor = vec3(0., 0.15, 0.15);

    vec3 col = mix(startColor, endColor, smoothstep(0.0, 1.0, uv.y));


    float x = (1. - uv.x * 0.6)*0.6;
    float y = (1.0 - abs(uv.y * 2.0 - 1.0))*1.5;
    col +=stars(uv, 20.);
    col += vec3(x *0.4, y, x) * v*1.2;



    fragColor= vec4(col,.5);


}
