#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    //Color
    vec3 bgColor = vec3(0.95);
    vec3 logoColor = vec3(0.05);

    //AspectRatioUV
    vec2 uv = fragCoord.xy - 0.5 * iResolution.xy;
	uv = 2.0 * uv.xy / iResolution.y;

    //Rotation
    float angle = sin(iTime) * 0.02 *PI;
    uv *= mat2(cos(angle), sin(angle),-sin(angle),cos(angle));
    //Height Offset
    uv.y += cos(iTime)*0.05;

	vec3 pixel = bgColor;

    float circle = length(uv-vec2(0.0));
    float horiLine = abs(uv.x + 0.28);
    float vertLine = abs(uv.y + 0.32);

    pixel = mix(logoColor, pixel, clamp(((circle-0.8) * 100.0),0.0,1.0));
    pixel = mix(bgColor  , pixel, clamp((horiLine -0.13) *100.0,0.0,1.0));
    pixel = mix(bgColor  , pixel, clamp((vertLine-0.08) *100.0,0.0,1.0));

    //EndResult
    float alpha = 1.0;
	fragColor = vec4(pixel, alpha);
}
