float getLatitude( vec2 pos, float radius ) {
    return acos(length(pos)/radius);
}


float getLongitude( vec2 pos, float radius ) {
    if (pos.x > 0.0) { return 0.5*atan(pos.y/pos.x)+2.36; }
    else { return 0.5*atan(pos.y/pos.x)+0.8; };
}

// not used
float getLongitude( vec2 pos, vec2 mouse, float radius ) {
    if (pos.x > 0.0) { return 0.5*(atan(pos.y/pos.x)- atan(mouse.x/mouse.y))+2.36; }
    else { return 0.5*(atan(pos.y/pos.x)-atan(mouse.x/mouse.y))+0.8; };
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r =  2.0*vec2(fragCoord.xy - iMouse.xy)/iResolution.y;

    // did not end up using this
    float mouseX= iMouse.x/iResolution.x;
    float mouseY= iMouse.y/iResolution.y;
    vec2 mouse = vec2(iMouse.x, iMouse.y);
    mouse =  2.0*vec2(mouse.xy - 0.5*iResolution.xy)/iResolution.y;

    vec2 p = vec2(fragCoord.xy / iResolution.xy);
	vec3 bgCol = texture(iChannel0, p).xyz; 
    //bgCol = vec3(0.0);
	vec3 colBlue = vec3(0.216, 0.471, 0.698);
	vec3 colRed = vec3(1.00, 0.329, 0.298);
	vec3 colYellow = vec3(0.867, 0.910, 0.247);

	vec3 pixel = bgCol;

	float radius = iTime/50.0;
    if (iTime > 35.0) {
        radius = 0.7;
    }
    radius = 0.5 + 0.05*cos(iTime/1.5);

    // draw sphereish thing
	if( r.x*r.x + r.y*r.y < radius*radius ) {
        pixel = colBlue * 0.8 * getLatitude(r, radius);
        pixel += vec3(1.0,1.0,1.0)* 0.33 * getLongitude(r, radius);
    }

    // draw glow behind
    if( r.x*r.x + r.y*r.y >= radius*radius && r.x*r.x + r.y*r.y < radius*radius+0.5) {
        pixel +=  colRed * smoothstep(0.0, 0.5, 0.5 - (r.x*r.x + r.y*r.y - radius*radius));
        //pixel +=  colRed * (0.5 - (r.x*r.x + r.y*r.y - radius*radius)); 
	}


	fragColor = vec4(pixel, 1.0);
}
