const float PI=3.14159265359;
float PHI=1.61803398874989484820459;// Î¦ = Golden Ratio

float gold_noise(in vec2 xy,in float seed){
    return fract(tan(distance(xy*PHI,xy)*seed)*xy.x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

    vec2 coord=fragCoord.xy/iResolution.xy;

    vec3 color=vec3(45./255.,48./255.,71./255.);

    // lines
    float range[2];// range array
    range[0]=.610;// y range
    range[1]=.475;// x range

    float time=iTime;

    for(int i=0;i<3;i++){
        vec2 ogPos=vec2(coord.x,coord.y);

        coord.y+=cos(coord.x*3.*coord.y*2.+time)*.05*cos(time*.5)*((coord.x+1.)/2.)*float(i+1)*.3;
        coord.x+=cos(coord.y*3.*coord.x*2.+time)*.05*cos(time*.5)*((coord.y+1.)/2.)*float(i+1)*.3;

        // pseudo random texture :O
        coord.y+=gold_noise(gl_FragCoord.xy,coord.x/10.)*.003;
        coord.x+=gold_noise(gl_FragCoord.xy,coord.y/10.)*.003;

        float yVal=floor(coord.y*150.);
        float xVal=floor(coord.x*150.);
        if(
            coord.y>.5-range[0]/2.&&
            coord.y<.5+range[0]/2.&&
            coord.x>.5-range[1]/2.&&
            coord.x<.5+range[1]/2.
        ){
            float testFor=1.;
            if(
                distance(vec2(cos(coord.y*PI*2.*15.),0.),vec2(testFor,0.))<.1||
                distance(vec2(cos(coord.x*PI*2.*15.),0.),vec2(testFor,0.))<.1
            ){
                color[int(mod(float(i),3.))]=1.;
            }
        }

        coord=ogPos;
    }

    fragColor=vec4(color,1.);

}
