/*
Simple black-red-yellow-white gradient
*/

float linearstep(float edge0, float edge1, float x)
{
    float t = (x - edge0) / (edge1 - edge0);
    return clamp(t, 0.0, 1.0);
}


// Argument t varies in range [0.0; 1.0]
vec3 ColorFunction(float t)
{
    float red = linearstep(0.0, 0.333, t);
    float green = linearstep(0.333, 0.666, t);
    float blue = linearstep(0.666, 1.0, t);
    return vec3(red, green, blue);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Get gradient color
    vec3 color = ColorFunction(uv.x);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
