void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float zoom = iMouse.x/iResolution.x;
    vec2 zoomUV = ceil(uv)*.5;
    vec2 finalUV = mix(uv,zoomUV,zoom);
    vec4 col = texture(iChannel0,finalUV);

    // Output to screen
    fragColor = vec4(col);
}
