void mainImage(out vec4 O, vec2 I)
{
    //Center
    O = pow((O.z = length(I -= (O.xy = iResolution.xy) * .5)/O.y)*
    //Texture with polar coordinates
    texture(iChannel0, log(O.z) - iTime - vec2(0, sign(I)*atan(I.yy,abs(I))/1.57)),
    //Purple Color
    vec4(1,2,.4,1));
}
