void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u+u - R ) / R.y;
    int  t = iFrame%2;
    if (U.y<0.) t = ( int(u.y) + int(u.x) + t ) % 2;
    O = length(U) > .7 
            ? t > 0 ? vec4(1,0,0,1) : vec4(0,1,1,1) 
            : t > 0 ? vec4(0,1,0,1) : vec4(1,0,1,1);
}
