void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    //Checkerboard Pattern by Alundra https://www.shadertoy.com/view/lt2XWK
    float Size = 10.0;
    vec2 Pos = floor(fragCoord / Size);
    float PatternMask = mod(Pos.x + mod(Pos.y, 2.0), 2.0);

    //Variant style (modified version):
    //float Size ) 20.0;
    //vec2 Pos = floor(fragCoord / Size);
    //float PatternMask = mod(sqrt(Pos.x / 10.0 + max(19.0, iTime * 8.0)) * 30.0 + mod(sin(Pos.x - Pos.y) / 4.0, 2.0), 2.0);

    // Time varying pixel color
    vec3 col = vec3(1.0, 1.0, 1.0);

    float f = cos(fract(sign(sin(0.5 - uv.x + sin(iTime) * 10.0) * tan(30.0) -  cos (0.75))));

    //Colors

    vec3 c = vec3(0.1, 0.05, 1.0);

    vec3 c2 = vec3(1.0*sin(iTime), 0.04, 1.0);

    //Wave shape

    float speed = 10.0;

    float r1 = atan(uv.y - 1.0) - cos(uv.x + uv.y + 1.0 * iTime * speed);

    float r = sin(floor(log(tan(uv.x -  1.0) + atan(uv.y - 1.0) * sin(uv.x + uv.y + 1.0 * iTime * 10.0) - atan(uv.y - 1.0) - tan(r1))));

    float sm = smoothstep(0.1, 0.4, uv.x - 0.5);

    //Fade bottom

    float ch = tan(uv.x - 0.5);

    r * ch;

    col *= floor(c) + c2 + r + f;

    col *= uv.y + 0.1 * PatternMask;

    // Output to screen
    fragColor = vec4(col,1.0);
}
