// https://www.shadertoy.com/view/XllyD4
#define M_PI 3.14159265358979323846264338327950288

vec3 getPureHue(float hue){

    hue = mod(hue, 1.0); // not required here but useful for copy-and-pasting

    hue *= 6.0;

    float hr = min(hue, 6.0 - hue);
    float hg = mod(abs(hue - 2.0), 6.0);
    float hb = mod(abs(hue - 4.0), 6.0);

    float r = clamp(2.0 - hr, 0.0, 1.0);
    float g = clamp(2.0 - hg, 0.0, 1.0);
    float b = clamp(2.0 - hb, 0.0, 1.0);

    return vec3(r, g, b);
}

vec3 hsv(float h, float s, float v){

    return mix(vec3(0.0), mix(getPureHue(h), vec3(1.0), s), v);
}

vec3 getColor(vec2 uv){

    float angle = atan(uv.x, uv.y) / M_PI * 0.5 + 0.;

    return hsv(angle, 1.0 - dot(uv, uv), 1.0);
}

vec2 getUv(vec2 fragCoord){

    float resolution = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord - 0.5 * (iResolution.xy - resolution)) / resolution - 0.5;
    return uv * 1.25;
}

float getValue(vec2 uv){

    const float l = 0.25;
    float uvSqrLen = dot(uv, uv);
    return 0.5 * (step(l, 0.5 - uvSqrLen) + smoothstep(l + 0.125, l, uvSqrLen));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = getUv(fragCoord);

    vec3 c = getColor(uv * 2.0);

    const float d = .25;
    float v = 0.0;
    v += getValue(getUv(fragCoord + vec2(-d, -d)));
    v += getValue(getUv(fragCoord + vec2(-d,  d)));
    v += getValue(getUv(fragCoord + vec2( d, -d)));
    v += getValue(getUv(fragCoord + vec2( d,  d)));
    v /= 4.0;

    fragColor = vec4(c * v, 1.0);
}
