void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy /iResolution.xy;

    vec2 q = uv - vec2(0.34, 0.7);

    // Time varying pixel color
    vec3 col = mix( vec3(1.0, 0.4, 0.1), vec3(1.0, 0.8, 0.3), sqrt(uv.y) );

    float r = 0.2 + .1 * cos( atan(q.y, q.x) * 10.0 + 20.0 * q.x + 1.0);

    col *= smoothstep(r, r + 0.01, length(q));

    r = 0.015;
    r += 0.002 * cos(120.0 * q.y);

    r += exp(-50.0 * uv.y);
    col *= 1.0 - ( 1.0 - smoothstep( r, r + 0.001, abs(q.x - 0.2 * sin(q.y * 2.0)) )) * (1.0 - smoothstep(0.08, 0.1, q.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
