renderpass.code:
/*
    Restart | Backspace  -- Clear the canvas, reset CC to White.
    W / Q                -- Set CC to White / Black.
    R / E                -- Set CC to Red / Turquoise.
    G / F                -- Set CC to Green / Purple.
    B / V                -- Set CC to Blue / Vomit.
    Mouse Drag           -- Paint.
*/


void  mainImage  (out vec4 o, vec2 p) {
    o = texelFetch(iChannel0, ivec2(p), 0);
}

#define  iMouse_down  (iMouse.z > 0.)
#define  onKey(K)     (texelFetch(iChannel1, ivec2((K), 1), 0).x > 0.)
#define  CC           vec3(texelFetch(iChannel0, ivec2(0, 0), 0))

#define  C_W  vec3(0.1)
#define  C_R  vec3(0.3, 0., 0.)
#define  C_G  vec3(0., 0.15, 0.)
#define  C_B  vec3(0., 0., 0.35)


vec3  Paint  (float dist) {
    return max(0., 0.002 * (40. - dist)) * CC;
}


void  mainImage  (out vec4 o, vec2 p) {
    o = texelFetch(iChannel0, ivec2(p), 0);
    bool reset = iFrame < 20 || onKey(8);
    bool store_CC = p.x == 0.5 && p.y == 0.5;
    bool view_CC = p.x < 20. && p.y < 20.;

    if (store_CC) {
        if (reset)      o = vec4(C_W, 1.);
        if (onKey(87))  o = vec4(C_W, 1.);
        if (onKey(81))  o = vec4(-C_W, 1.);
        if (onKey(82))  o = vec4(C_R, 1.);
        if (onKey(69))  o = vec4(-C_R, 1.);
        if (onKey(71))  o = vec4(C_G, 1.);
        if (onKey(70))  o = vec4(-C_G, 1.);
        if (onKey(66))  o = vec4(C_B, 1.);
        if (onKey(86))  o = vec4(-C_B, 1.);
    } else if (view_CC) {
        o = vec4(0.5 + 8888. * CC, 1.);
    } else {

        if (reset) {
            o = vec4(0.5, 0.5, 0.5, 1.);
            return;
        }

        if (iMouse_down) {
            float dist = length(iMouse.xy - p);
            o += vec4(Paint(dist), 0.);
        }

    }
}
