mat2 r2(float t)
{
	return mat2(cos(t), -sin(t),sin(t),cos(t));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord - iResolution.xy*0.5) / iResolution.y;
	vec2 uvn = fragCoord/iResolution.xy;
    float time = iTime;

	float brightness = 0.1;
    uv *= r2(time);
	uv -= vec2(.1);

    uv *=(-.5*sin(time)+.5)*.3+1.;
    uv *= r2(length(uv*100.*(sin(time*1.6)*.2+.5)));

	vec3 col1 = vec3(.5+abs(sin(time)+1.)*.5,cos(time/2.)+1.,.5+sin(time+.5)*.5), col2 = vec3(sin(time/2.)+1.,
	sin(time*2.)+1.,0);
		vec3 fcol = mix(col1,col2,uvn.y);

	uv *= r2(40.*(sin(time*1.6)*.2+.5));
	uv -= vec2(.1);
	uv.x -= .008;

   float len = length(uv);
   vec3 col = vec3(brightness/len)*fcol;
   fragColor = vec4(col, 1.0);
}
