float Mark(vec2 uv) {
    uv = abs(uv - .5);
    return 1.;
}

float Rectangle(vec2 uv, vec2 Size, vec2 Pos, float Blur)
{
    uv -= Pos;
    Size /= 2.;

    float XBand = smoothstep(-Size.x - Blur, -Size.x + Blur, uv.x) * smoothstep(Size.x + Blur, Size.x - Blur, uv.x);
    float YBand = smoothstep(-Size.y - Blur, -Size.y + Blur, uv.y) * smoothstep(Size.y + Blur, Size.y - Blur, uv.y);
    return XBand * YBand;
}

float Box(vec2 uv)
{
    const vec2 Size = vec2(.5, .25);

    float Mask = Rectangle(uv, vec2(.5, 1.), vec2(-.25, 0.), .001);
    uv += Size * .5;
    uv = mod(uv + Size * .5, Size) - Size * .5;
    uv = abs(uv);
    return Mask * clamp(Rectangle(uv, vec2(.05), Size * .5 - .025, .00001) - Rectangle(uv, vec2(.325), Size * .5 - .175, .0001), 0., 1.);

}

#define Text(uv, Size, Pos, Text, Output)\
{\
    vec2 TextUv = round((uv - Pos) / Size * 20.) * .05;\
    for (float i = 0.; i < float(Text.length()); i++) {\
        float Current = Text[int(i)];\
        vec2 Code = vec2(\
            mod(Current, 16.),\
            15. - floor(Current / 16.)\
        );\
        \
        Output += Rectangle(TextUv, vec2(1.), vec2(0.), .001) * texelFetch(iChannel0, ivec2((TextUv + Code) * 64. + 32.), 0).x;\
        TextUv.x -= .5;\
    }\
    \
    Output = ceil(Output - .7);\
}

float TriangleWave(vec2 uv, vec2 Size, vec2 Pos)
{
    uv -= Pos;
    uv /= Size;

    float In[8] = float[8](
        84.,
        114.,
        105.,
        97.,
        110.,
        103.,
        108.,
        101.
    );
    float col = 0.;
    Text(uv, vec2(.1, Size.x / Size.y * .1), vec2(-.4375, Size.x / Size.y * .1 -.55), In, col)

    float time = iTime + uv.x * BPM * .005;
    float At = mod(time * BPM, 8.) + TrianglePatterns[int(mod(time * BPM / 8., Total))] * 8.;
    float Sum = TriangleTrack(At, uv.x * .02, 1.);

    col = clamp(col + smoothstep(.006, .005, abs(uv.y + Sum * .2)) * smoothstep(.501, .5, abs(uv.x)), 0., 1.);
    return col;
}

