float Background(vec2 uv)
{
    float Reaction = cos(iTime * BPM * pi);

    uv *= mat2(cos(-.2), -sin(-.2), sin(-.2), cos(-.2));
    uv *= 8. + Reaction;
    uv += iTime * 5. + Reaction * .8;
    uv = mod((uv + .5), vec2(3.6, 1.)) - .5;
    float In[13] = float[13](
        67.,
        72.,
        73.,
        80.,
        32.,
        69.,
        78.,
        71.,
        73.,
        78.,
        69.,
        32.,
        73.
    );

    float Color = 0.;
    Text(uv, vec2(.5), vec2(0.), In, Color)

    return Color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5)/iResolution.y;

    float Col = TriangleWave(uv, vec2(.5, .25), vec2(-.25, .375));
    Col += SquareWave(uv, vec2(.5, .25), vec2(-.25, .125));
    Col += SawWave(uv, vec2(.5, .25), vec2(-.25, -.125));
    Col += PulseWave(uv, vec2(.5, .25), vec2(-.25, -.375));
    Col += Background(uv) * .2;
    Col += Box(uv);

    fragColor = vec4(vec3(Col), 1.);
}

vec2 mainSound( int samp, float time )
{
    float At = mod(time * BPM, 8.);

    int PatternAt = int(mod(time * BPM / 8., Total));
    float TriangleAt = At + TrianglePatterns[PatternAt] * 8.;
    float SquareAt = At + SquarePatterns[PatternAt] * 8.;
    float SawtoothAt = At + SawtoothPatterns[PatternAt] * 8.;
    float PulseAt = At + PulsePatterns[PatternAt] * 8.;

    return vec2(
        (TriangleTrack(TriangleAt, 0., 0.) + SquareTrack(SquareAt, 0., 0.) + SawtoothTrack(SawtoothAt, 0., 0.) + PulseTrack(PulseAt, 0., 0.)) / 8.
    );
}
