    //quesion is what's different between uv and iResolution.xy;




    float radius =  0.2 * uv.y;

    //float radius = 0.2 * iResolution.y;


    //---------------------------------------------  

    // a circle = ( center - uv) - rad
    float d = length(uv - circle_pos) - radius;





   vec4 col = vec4 (0.2,0.1,d,d);

