const float pi = acos(-1.);

float atan2(float y, float x){
    if(x >= 0.){
        return atan(y/x)/pi/2.+.75;
    }
    return (pi+2.*atan(y/x))/4./pi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //======================
    //parameters
    int amount = 1;
    float width = 5.;
    float speed = 1.;
    vec3 color1 = vec3(1.0, 1.5, 1.0);
    vec3 color2 = vec3(-0.1, 0.0, 0.1);
    //======================

    vec2 R = iResolution.xy;
    vec2 uv = fragCoord-R/2.;
    vec3 col;
    float circle = sqrt(uv.x*uv.x+uv.y*uv.y);
    float d = (circle/100.);
    d -= iTime*speed;

    float mask = smoothstep(R.y/2.+width*4., R.y/2.-width*4., circle);

    col = vec3(sin((2.*pi*atan2(uv.y, uv.x)+d*width)*float(amount))+1.)/2.;
    col = color1+col*(color2-color1);
    col *= mask;
    fragColor = vec4(col,1.0);
}
