// see also: https://www.shadertoy.com/view/3tSGWy
//           https://www.shadertoy.com/view/tst3DX

#define R iResolution.xy
  #define S(d,r) smoothstep( 3.,0., (d)*R.y -r )  
//#define S(d,r) max( 0., 1. -(d)*R.y/2. +r )  


float line(vec2 p, vec2 a,vec2 b, float r) { 
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.), // proj coord on line
          d = length(p - b * h);                    // dist to segment
    return S(  d , max(0.,r-1.) ) * min(r,1.);
}


void mainImage( out vec4 O,  vec2 u )
{
    O -= O;
    vec2 U = ( 2.*u - R ) / R.y;
    float tau = 6.2832, n = U.x < 0. ? 32. : 128.,  // number of lines
          a, l = length(U);
    l *= U.y > 0. ? .5 : 4.*l;
#define Line O += (1.-O) * line( U, vec2(0), cos(tau*a-vec2(0,1.57)) , l )

    if ( l*tau/n < 5./R.y ) 
        for ( a = 0.; a < 1.; a+=1./n )             // draw all lines
            Line;
               // optim: when lines are separated, e can do this loopless
    else a = ( round( atan(U.y,U.x)/tau*n )  ) / n,
            Line;

O = pow(O, vec4(1./2.2) );                          // to sRGB
}
