void mainImage(out vec4 O, in vec2 U) {
    vec2 r = iResolution.xy;
    O = vec4(1);

    U = (U - r / 2.) / r.y + .5;
    float d = 9.,
          a = 3.,
          b = 2.,
          x = 1. / a,
          y = 1. / b, t, q;
    for (int i = 0; ++i < 9;){
        q = U.x - x;
        t = clamp(q, 0., 1. / a);
        d = min(d, length(vec2(q - t, U.y - y)));
        bool f = q * a < .5;
        a *= 3.; b *= 2.;
        x += (f ? -2. : 4.) / a;
        y += (f ? -1. : 1.) / b;
    }
    O -= min(d / 0.003, 1.);
}
