void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = 2.*(fragCoord*2.-iResolution.xy)/iResolution.y;
    vec2 m = 2.*(iMouse.xy*2.-iResolution.xy)/iResolution.y;

    vec2 v = u;
    v = v/dot(v,v)-vec2(.5,0);//bring the black point at infinity into view
    v = v/dot(v,v);
    v = cos(atan(v.y,v.x)+iTime-vec2(.0,.5)*3.14159265)*length(v);//rotate image

    vec4 o = texture(iChannel0,v);
         o = mix(o,vec4(1),step(0.,1.-8.*length(u-vec2(0,0))));//white point
         o = mix(o,vec4(0),step(0.,1.-8.*length(u-vec2(2,0))));//black point

    fragColor = o;
}
