#define pi 3.14159

float func(float a, float b) { return tanh(a * b) / tanh(a); }
float thc(float a, float b) { return func(a, cos(b)); }
float ths(float a, float b) { return func(a, sin(b)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float zoom = 5. + 2. * thc(2., 0.8 * iTime);

    // Height of pulses (in [-0.5, 0.5] range)
    float h = 1. / zoom;// + 0.05 * cos(10. * uv.x + iTime);   

    // Width of pulses
    float w = 1.5 + 0.5 * ths(4., iTime);

    // Spacing of pulses
    float sp = 3. + thc(4., iTime);

    // Sharpness / squareness of pulses (larger numbers -> squarer pulses)
    float shrp = 3.;

    // Translate x
    float t = 0.; // iTime;

    // x value (works well with polar co-ordinates)
    float x = zoom * uv.x;

    // y value
    float y = w * (mod(x + t, sp) - 0.5 * sp);
    y = clamp(y, -1., 1.);
    y = 0.5 * y * (3. - y * y);

    // (y = 0.5 * cos(pi * y) works well on it's own)
    y = 0.5 * cos(pi * y) + 0.5;
    // (smoothstep makes the base of the pulses smoother (I think))
    y = smoothstep(0., 1., y); 
    y = func(shrp, y) - 0.5;

    // Shape (ty fabrice) 
    float v = -uv.y + 2. * h * y;
    float s = 0.5 * smoothstep(-.7,.7, v/fwidth(v) );
    //float px = 2./iResolution.y;
    //s = 0.5 * smoothstep(-px, px, -uv.y + 2. * h * y);

    // Red lines
    float s2 = 0.75 * (1. + thc(2., iTime)) * smoothstep(-0.005, 0.005, -abs(abs(uv.y)-h) + 0.001);

    fragColor = vec4(s2, s, s, 1.);
}
