#define PI 3.1415926535

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    // transfer the range (-1, +1)
    st -= vec2(0.5, 0.5);
    st *=2.0;

    vec3 col = vec3(0.0);

    float r = distance(st, vec2(0.0));
    float a = atan(st.y/st.x)-r*r*0.5;

    a += iTime;
    float r_sin = abs(sin(r*4.0+iTime));
    float r_cos = abs(cos(r*4.0+iTime));

    float func;
    func = cos(a);
    //func = cos(a*3.);
    //func = abs(cos(a*3.));
    //func = abs(cos(a*2.5))*0.5+0.3;
    //func = abs(cos(a*12.));
    //func = abs(sin(a*3.0));
    //func = abs(cos(a*12.)*sin(a*3.))*.8+.1;
    func = abs(cos(a*12.)*sin(a*3.))*.5+0.5;
    //func = smoothstep(0.0, 1., cos(a*10.))*0.2+0.5;

    //col = vec3(abs(cos(a*12.)*sin(a*3.)));
    //col = vec3(func);
    //col = vec3(1.0 - smoothstep(func,func+0.02,r) );
    col += vec3(smoothstep(func,func+0.02,r_sin));
    col *= vec3(1.0, r_cos*0.4, 0.4*r);

    fragColor = vec4(col,1.0);
}
