float Shadow(float time,vec2 uv) {
    float velx=1.0;
    float freqx=2.0;
    float vely=-1.0;
    float freqy=2.0;
    float timeshiftx=(time*velx)+(uv.x*freqx);
    float timeshifty=(time*vely)+(uv.y*freqy);
    float shadow=0.5+cos(timeshiftx)*sin(timeshifty)*0.5;
    return shadow;

 }


