#define T(a) abs(fract( ( a+vec2(1.,.5 )) *1.5 ) -.5 )

void mainImage( out vec4 O, in vec2 u )
{
    vec2 U = u/iResolution.y/2. + vec2(iTime/16.,0), V, t2=U-U;
    O-=O;
    for(int k = 0; k < 6; k++)
        V = U,
        U = (U+t2)/1.5,  
        t2 = abs(T(U+.5)-.5),
        U = .5+t2-T(U.yx),
        O.x = abs( U.y-U.x -.5 +O.x ) / 2.,
        O = V.x < V.y                ? O.yzxw
          : U.x > U.y || t2.x > t2.y ? O.zyxw / 2.
          :                           .5 - O.xxxx;

    O = abs(O+O);
}
