#define pi acos(-1.)
#define deg pi/180.
#define time iTime*pi/2./4.

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

float eyet(vec2 uv, float t_) {
    float col = (0.);
    vec2 r = vec2(15.);
    float ea = 1.;
    //uv -= 0.5;
    //uv.x *= ar;
    //uv *= 1.-sin(time);
    //col *= sin(uv.y*920.);
    //col += sin(uv.x*809.+sin(time));
    float as = floor(fract(uv.y/pi*r.y/1.+0.25)-0.5)+0.5;
    //float t = t_*pi*2.*(floor(fract(uv.y/pi*r.y/1.+0.25)-0.5)+0.5);
    float t = t_*pi*2.;
    t = t*as;
    float s = (sin(uv.y*r.y*2.+sin(uv.x*r.x+t/pi*2.)));
    float s1 = (sin(uv.y*r.y*2.-sin(uv.x*r.x+t/pi*2.)));
    col += step(s1*s,0.);
    //uv.x += sin(time*5.+uv.y*15)*0.02;
    vec2 ua = vec2(sin(t/pi*2.)*2.,cos(t/pi*2.)*0.5);
    col -= 1.-step(sin(uv.x*r.x*2.+t/pi*4.+pi/1.*as+ua.x)*sin(uv.y*r.y*2.+pi/2.+ua.y),0.8);
    //col -= 1.-step(sin(uv.x*r.x*2.+sin(time*4.+uv.x*5.+uv.y))*sin(uv.y*r.y*2.+pi/2.+sin(time*2.+uv.y*5.+uv.x)),0.8);
    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 R = iResolution.xy;
    float ar = R.x/R.y;
    vec3 col = vec3(0.);
    uv -= 0.5;
    uv.x *= ar;
    float z = 24.;
    z += sin(time*2.)*10.+15.;
    col += eyet(uv*3.,time);
    fragColor = vec4(col,1.0);
}
