float PI = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float TAU = PI * 2.;
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 uv2 = fract(uv * 10.);
    float th = atan(uv.y, uv.x);
    float t = sin(iTime);
    float m = 0.5 + 5. * 0.5 * (1. + sin(iTime));
    float n = 0.1 + 0.5 * 0.5 * (1. + sin(iTime * 0.4));

    float f = pow(0.5 * (1. + sin(th * 2. + pow(length(uv), n) * TAU * 15. - iTime * 8.)), m);
    float p = 0.5 * (1. + sin(th * 33. + length(uv) * TAU * 5. + iTime * 20.));
    vec3 col = vec3(f * p);

    fragColor = vec4(col,1.0);
}
